function [valz] = zigzag(x,k,m)
        if k*x ~= round(k*x)
            valz = m*(1/2+((ceil(k*x)+floor(k*x))./2 - k*x).*(-1)^(ceil(k*x)));
        elseif mod(k*x,2) == 0
            valz = 0;
        else 
            valz = m;
        end
end

